import React from'react'
import { useState,Fragment} from 'react'
export default function App(){
  let elments=["element1","element2","element3",'element4',"element5"];
  const[clr,setclr]=useState()
  return(
    <Fragment>
      <select name='slct' onChange={(e)=>setclr(e.target.value)}>
        {elments&&elments.map((c,i)=>{
          return <option key={i} value={c}>{c}</option>
        })}
      </select>
      {clr?<div style={{color:`${clr}`}}>votre color choisir :{clr}</div>:''}
    </Fragment>
  )
}
