import React,{useState} from 'react'




export default function App(){

    const [numVol, setNumVol]=useState()

    const [vol,setVol]=useState(vols[0])

    const [total,setTotal]=useState(0)


    function rechercherVol(){

        setVol(vols.find((x)=> x.numVol==numVol ) )
        setTotal( vol.places.filter((y)=> y.libre===false).reduce((s,p)=> s+p.tarif,0))

    }

    function reserverPlace(i){

        const v= vols.find((x)=> x.numVol=== vol.numVol)

        const p=v.places
    
        p[i].libre=false
    
    
    
    
        setVol(previousState => {
          return { ...previousState, places:p  }
        });


        setTotal( vol.places.filter((x)=> x.libre===false).reduce((s,p)=> s+p.tarif,0))



    }

    function LignePlace(props){

        return(
            <tr key={props.k}>
                <td>{props.place.numPlace}</td>
                <td>{props.place.tarif}</td>
                <td>{
                    props.place.libre
                    ?<button onClick={()=> reserverPlace(props.k)} >Reserver</button>
                    :<button disabled >Reserver</button>
                    }
                </td>
            </tr>
        )
    }



    return(

        <div>
            <h2>Réservation de places</h2>
            <h2>
                Numero de vole: 
                <input type="text" onChange={(e)=> setNumVol(e.target.value)} ></input>
                <button onClick={rechercherVol}>Rechercher</button>
            </h2>
            <h2>Ville de départ: {vol?vol.depart:''} </h2>
            <h2>Ville d'arrivée: {vol?vol.Arrivee:''} </h2>
            <h2>Prix total des reservations:{total}
                
                
            </h2>
            <h2>Liste des places du vol: </h2>

            <table>
                <thead>
                    <th>N° place</th>
                    <th>Tarif</th>
                    <th> Action</th>
                </thead>

                {
                    vol?
                    vol.places.map((x,i)=> <LignePlace k={i} place={x}/> )
                    :''
                }


            </table>


        </div>
    )
}
