import React,{useState} from 'react';
const trains=[
  {id:'t1',nom:'Buraq',villes:[{idV:1,nomV:'fes'},{idV:2,nomV:'casa'}]},
  {id:'t2',nom:'Gazel', villes:[{idV:1,nomV:'casa'},{idV:2,nomV:'salle'}]},
  {id:'t3',nom:'Eagle-Wins', villes:[{idV:1,nomV:'rabat'},{idV:2,nomV:'ifran'}]},
  {id:'t4',nom:'Fennec', villes:[{idV:1,nomV:'meknes'},{idV:2,nomV:'taza'}]},
  ]
export default function App() {
    const [id,setId]=useState()
    const [list,setLists]=useState()
    const [ville,setVille]=useState()
    const[editStat,seteditStat]=useState(false)
  

    function rechercher(){
      if(id){
        setLists(trains.find((t)=>t.id===id))
      }
    }

    function ajouter_ville(){
      list.villes.push({idV:list.villes.length+1,nomV:ville})
      setLists((prev)=>({
        ...prev,villes:list.villes
      })
      )
  }
  
  function handlEdit(id){
    seteditStat(id)
  }
  function handleUpdate(){
      seteditStat(false)
  }

//   Update:function(state,action){
//     state.tbl.map(tbl=>{
//         if(tbl.id==action.payload.id){
//             tbl.nomP=action.payload.nomP;
//             tbl.prixP=action.payload.prixP;
//         }
//     })
//  }

    function supremier(i){
       list.villes.splice(i,1)
       setLists({...list,villes:list.villes})
    }
 
  return (
    <div>
    <div >
      <input type='text' onChange={(e)=>setId(e.target.value)}/>
      <button onClick={rechercher}>rechercher</button>

       <div ><b>Nom</b> {list?list.nom:""}</div>
       <div ><b>Depart</b> {list?list.villes[0].nomV:""}</div>
       <div ><b>Termine</b> {list?list.villes[list.villes.length - 1].nomV:""}</div>

      <div>
        <b>Nouvelle ville de passage</b>
        <input type='text'onChange={(e)=>setVille(e.target.value)} />
        <button onClick={ajouter_ville} >Ajouter la ville</button>
      </div>
      <h2>liste des villes de passage</h2>
      <table>
        <tr>
          <th>nom de ville</th>
          <th>id de passage</th>
          <th>action</th>
        </tr>
        {list?list.villes.map((l,indx)=>{
          return editStat===indx+1?<tr>
            <td><input type='text'/></td>
            <td>{l.idV}</td>
          </tr>
          :<tr key={indx}>
            <td>{l.nomV}</td>
            <td>{l.idV}</td>
            <td>
              <button onClick={()=>supremier(indx)}>delete</button>
              <button>Update</button>
            </td>
          </tr>
        }):'vide'}
      </table>

    </div>
    </div>
  )
}
