import React, { useState } from 'react'
export default function GaimeRandom({click}) {
  const[rand,setRand]=useState()
  const[essais,setessais]=useState(0)

    function handleRandom(){
        setRand(Math.floor(Math.random()*6+1))
        setessais(essais+1)
    }

    function Initialiser(){
        setRand(Math.floor(Math.random()*6+1))
        setessais(essais+1)
    }
  return (
    <div className='mt-4'>
        <div>
            <img src={click===0?'./images/init.jpg':`./images/number${rand}.jpg`}  alt='err' className='border'/>
            <div>jeu de dai</div>
            <div>face:{rand} </div>
            <div>nomre d'essais :{essais}</div>
            {click==rand?(
                <>
                <div><button>Initialiser</button></div>
                <b>Bravo!!</b>
                </>
            ):(
                  <div><button onClick={()=>handleRandom()}>jouer</button></div> 
            )}
         
        </div>
    </div>
  )
}
