// import img1 from"../images/face1.PNG";
export default function JouRandom({cach}) {
  const[conter,setConter]=useState(0);
  const[face,setFace]=useState(null);
  function increment(){
    setConter(conter+1);
    setFace(Math.floor(Math.random()*6+1))
  }
 function init(){
  setConter(0)
  setFace(null)
}
  return (
 <div className='text-center' style={{border:"1px solid red",width:"50%",margin:"0 auto"}}>
    <img src={face==null?"./images/init.PNG":`./images/face${face}.PNG`} />
    <h2>Jeu de DÃ©...</h2>
    <p>face: {face}</p>
    <p>nomber d'essais:{conter} </p>
    {cach===face?<div>
      <div> <small>Bravo</small></div>
      <button onClick={()=>increment()}>jouer</button></div>:
      <div><button onClick={()=>init()}>inisialisier</button> </div>}
   {/* <div> <small>{cach===face?'Bravo':''}</small></div>
    <button onClick={()=>increment()}>jouer</button> */}
 </div>
  )
}
