import React from 'react'
import { useState } from 'react';

export default function App() {
  const list = [
    "Banana",
    "Apple",
    "Orange",
    "Mango",
    "Pineapple",
    "Watermelon"
    ];
    const[cherhcher,setChercher]=useState()

  return (
    <div>
      <input type='text' onChange={(e)=>setChercher(e.target.value)}/>
      <ul>
        {list?list.filter((f)=>f.includes(cherhcher)).map((r)=>{
          return <li>{r}</li>
        }):null}
      </ul>
    </div>
  )
}
