import React, { Component } from 'react'
import Contactitem from './Contactitem';
import Addcontact from './Addcontact';
class Contact extends Component {
    constructor(props){
        super(props);
        this.state={
            contactArry:[
                {
                    "name":'mohamed',
                    'tele':'064563563'
                },
                {
                    "name":'walid',
                    'tele':'064663537'
                },
                {
                    "name":'saad',
                    'tele':'064567534'
                },
            ]
        }
    }
   addNouvcontact=(contact)=>{
    let NouContact=this.state.contactArry;
    if(NouContact==''){
        alert('vide')
    }
    NouContact.push(contact);
    this.setState({ contactArry: NouContact});
   }

   deleCountact=(name)=>{
    let oldContact=this.state.contactArry;
    let NouContact=oldContact.filter((contact)=>contact.name !== name);
    console.log(NouContact)
    this.setState({ contactArry: NouContact});
   }
    render(){
        return (
            <div className='container'>
                <Addcontact addNouvcontact={this.addNouvcontact} />
                <div className="row" my-5>
                    <div className="col-md-6 mt-4 mx-auto">
                        <div className="card">
                            <table className='table'>
                                <thead>
                                    <tr>
                                        <th>Nom & Prenom</th>
                                        <th>Telephone</th>
                                        <th>Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                {
                               this.state.contactArry.map((contact,index)=>
                                <Contactitem key={index} contact={contact} deleCountact={this.deleCountact}/>
                                    )
                                }
                                  
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div>
           
          )
    }   
    }
export default Contact
