import React,{ Component } from "react";
import axios from "axios";
import './User.css'
class User extends Component {
    state={
        mayAr:[]
    }
    componentDidMount(){
        axios.get('https://jsonplaceholder.typicode.com/users')
        .then(res=>{
            this.setState({
                myVar:res.data
            })
        })
    }
    render() { 
            return(

                <div>
                    <h2>les donnes</h2>
                    {this.state.mayAr.map(users =><h3>{users.name}</h3>)}

                </div>
            );
        
    }
}
export default User;
