import React,{useState} from 'react'

export default function App() {
  const trains=[
    {id:'t1',nom:'Buraq',villes:[]},
    {id:'t2',nom:'Gazel', villes:[]},
    {id:'t3',nom:'Eagle-Wins', villes:[]},
    {id:'t4',nom:'Fennec', villes:[]},
    ]
  
    const [id,setId]=useState()
    const [train,setTrain]=useState()
    const [ville,setVille]=useState('')

    function rechercher(id){
      setTrain( trains.find((t)=> t.id==id))
    }
  return (
    <div>
      <h2>Gestion des trains</h2>
            <h2>Id: <input type="text" onChange={(e)=>setId(e.target.value)}/>
            <button onClick={rechercher}>rechercher</button>
            </h2>
            <h2>Nom du train: {train?train.nom:''} </h2>
            <h2>Volle de dÃ©part:{train && train.villes.length>0?train.villes[0]:''} </h2>
            <h2>Ville terminus: {train && train.villes.length>0?train.villes[train.villes.length-1]:''} </h2>
            <br></br>
    </div>
  )
}
