import React, { useState } from 'react'
const [products,setProducts]=useState([]);


class Products{
    constructor(n,p,q){
        this.name=this.n
        this.price=this.p
        this.amount=this.q
    }
} 

function FormAchat(props){
    const[nom,setNom]=useState('');
    const[prix,setPrix]=useState(0);
    const[quantit,setQantit]=useState(0);
   
   function ajouterPanier(){
    setProducts([...products,new Product(nom,prix,quantit)])

    }
    return(
        <form>
            <h2>Nom Prduit :</h2>
            <input type='text' onChange={(e)=>setNom(e.target.value)}/>
            <h2>Nom prix :</h2>
            <input type='text' onChange={(e)=>setPrix(e.target.value)}/>
            <h2>Nom quantit :</h2>
            <input type='text' onChange={(e)=>setQantit(e.target.value)}/>
            <button type='submit' onClick={ajouterPanier}>add</button>
        </form>
        
       
    )
}
