import React from 'react'
import axios from'axios'
import { useState } from 'react'
import { useEffect } from 'react'
import Postes from './components/Postes'
export default function App() {
  const[user,setUser]=useState([])
  const[vue,setVue]=useState(0)
  useEffect(()=>{
    axios.get(`https://jsonplaceholder.typicode.com/users`).then(
      (reslt)=>setUser(reslt.data)
    )
  },[])
  function Details(){
    setVue(1)
  }

  return (
    <div >
      <h3>le nomber des etulisateurs :{user.length}</h3>
        {user &&user.map((use)=>{
          return(
            <div className='bg bg-primary m-2 text-light col-6 ms-3'>
              <div><b>le nom :{use.name}</b></div>
              <div>email: {use.email}</div>
              <div>ville: {use.address.street}</div>
              <div><button onClick={Details}>Details posts</button></div>
              {}
            </div>
          )
        })}
      </div>
   
  )
}
