import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { AjouterAction,SuprmierAction } from './Redux/InitialAction'
export default function Affihcer() {
  const [valu,setValu]=useState()
  const dispatch= useDispatch()
    const content=useSelector((data)=>data.val)

    const handlClick=()=>{
      dispatch(AjouterAction({
        id:Math.floor(Math.random()*1000),
        elm:valu
      }))
      setValu("")
    }

    function handlDelete(iid){
      dispatch(SuprmierAction(iid))
    }

  return (
    <div className='container col-8 shadow-lg m-2 p-2 w'>
       <div className='d-flex mb-3'>
       <input style={{"width":"80%"}} type='text' className=' me-3 form-control' value={valu} onChange={(e)=>setValu(e.target.value)}/>
        <button className='w-20 btn btn-warning' onClick={handlClick}>Valider</button>
       </div>
        <div>
           
                {content?content.map((e)=>{
                    return <ul style={{"listStyleType":"none"}}>
                        <li key={e.id} className="d-flex  justify-content-around">
                          <table border={"1px"} width={"80%"}>
                            <tr>
                              <td>{e.elm}</td>
                              <td>  <button className='btn btn-danger' onClick={()=>handlDelete(e.id)}>DELETE</button></td>
                            </tr>
                          </table>
                        </li>
                    </ul>
                }):""}
            
        </div>
    </div>
  )
}
