import React, { Component } from 'react';
 class App extends Component {
  state={
    name:'Mohammmed',
    age:'20'
  }
  chang=()=>{
  this.setState({
  name:'Ali',
    age:'30'
    })
  }
  
  

  render() {
    return (
      <div>
        votre nom est : {this.state.name} <br/>
        <button onClick={()=>this.chang("walid",10)}>click</button>
      </div>
    )
  }
}
export default App











