import React from 'react'
import { useState } from 'react'
export default function App() {
    const curs=[{id:"marrchF",
    particip:[
      {nom:"doha",car:'F',destance:10000}
    ]
  },
  { id:"marrchH",
    participle:[
      {nom:"mohamed",car:'H',destance:30000}
    ]
  },
  { id:"maratonF",
    participle:[
      {nom:"salama",car:'F',destance:3000}
    ]
  },
  { id:"maratonH",
    participle:[
      {nom:"yassin",car:'H',destance:4000}
    ]
  },
   ];
   const [lists,setLists]=useState(curs);
   const [inpt,setinpt]=useState()
   setinpt(document.getElementById('inpt').value)
  return (
    <div>
        <div>
            <label>Cours :</label> <input type='text' id='inpt'/> <button> gouch </button><button> droit </button>
        </div>
        <div>Nom : <input type="text"/> </div>
        <div>
            <lable>Cathegorie :</lable>
            <input type="radio" name='rad'/> Homme
            <input type="radio" name='rad'/> Fammme
        </div>
        <div>
            <label> Liste des participle :</label><button>Ajouter</button>
        </div>
        <div>
            <table style={{border:"1px solid red"}}>
                <tr style={{border:"1px solid red"}}>
                    <th style={{border:"1px solid red"}}>id</th>
                    <th style={{border:"1px solid red"}}>Nom</th>
                    <th style={{border:"1px solid red"}}>idCourse</th>
                    <th style={{border:"1px solid red"}}>action</th>
                </tr>
                {
                inpt==="marrchF" ?
                  lists.map((e)=>
                  <tr style={{border:"1px solid red"}}>
                    <td style={{border:"1px solid red"}}>{e.id}</td>
                  </tr>
                  )  :"no"
                }
            </table>
        </div>
    </div>
  )
}
