import React from 'react'
import { useState } from 'react'
export default function App() {
  const velos = [
    {ref:'v1', marque:'ford', parcours:[] }
]

const [tab,setTab] = useState(velos)
const [autonomie,setautonomie]=useState()
const [kilometrage,setkilometrage]=useState()

function remplir(){
  tab.parcours.push({auto:autonomie,kilo:kilometrage})
  setTab((prev)=>({
    ...prev,parcours:tab.parcours
  }))

}
  return (
    <div>
      <input type="text"  placeholder='Enter autonomie' onChange={(e)=>setautonomie(e.target.value)} />
      <input type="text"   placeholder='Enter kilometrage' onChange={(e)=>setkilometrage(e.target.value)}/>
      <button onClick={()=>remplir()}>ajouter</button>
      <div>
        <table>
          <tr>
            <th>autonomie</th>
            <th>kilometrage</th>
          </tr>
          {tab.parcours?tab.parcours.map((r,index)=>{
            return <tr key={index+1}>
              <td>{r.auto}</td>
              <td>{r.kilo}</td>
            </tr>
          }):""}
        </table>
      </div>
    </div>
  )
}
