import React from 'react'
import { useParams } from 'react-router'
import { Link } from 'react-router-dom'

export default function SingleProdct({productsAp}) {
  const {id}=useParams()
  const product=productsAp.find((pr)=>pr.id===parseInt(id))
  return (
    <div>
      <div className="card shadow-sm col-3">
      <img className="bd-placeholder-img card-img-top"src={`../pictures/product.thumbnail`} alt="" />
      <div className="card-body">
      <p className="card-title">{product.title}</p>
      <p className="card-text">{product.price}</p>
      <Link to={'/'}>Aller ver la page d'acciel</Link>
      </div>
    </div>
    </div>
  )
}
