import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { useNavigate } from 'react-router'
import {AjouterAction} from "./Redux/actions/InitialAction"

export default function Ajouter() {
  const [name,setName]=useState()
  const [SurfaceArea,setSurfaceArea]=useState()
  const [Population,setPopulation]=useState()

  const count=useSelector(data=>data.py.length)
  const dispatch= useDispatch()
   const navigate=useNavigate()
  
  const handlClick=()=>{
    if(name!=="" && SurfaceArea!=="" && Population!==''){
      dispatch(AjouterAction({
  code:count+1,
  name:name,
  SurfaceArea:SurfaceArea,
  Population:Population
}))  
navigate('/')
  }else{
    alert('v')
  }
    } 

 
  return (
    <div className='d-flex justify-content-center'>
      <form className='container card shadow-lg m-2 p-2 col-8 d-flex flex-column gap-3'>

       <div className='d-flex flex-column gap-2'>
       <label className='text-success'>name</label>
        <input type="text" className='form-control' value={name} onChange={(e)=>setName(e.target.value)}/>
       </div>

       <div className='d-flex flex-column gap-2'>
        <label className='text-success'>SurfaceArea</label>
        <input type="text"className='form-control'  value={SurfaceArea} onChange={(e)=>setSurfaceArea(e.target.value)}/>
       </div>

       <div className='d-flex flex-column gap-2'>
        <label className='text-success'>Population</label>
        <input type="text"className='form-control'  value={Population} onChange={(e)=>setPopulation(e.target.value)}/>
       </div>

      <div className='d-flex justify-content-end mx-4'>
      <button className='btn btn-success mx-2' onClick={handlClick}>valider</button>
      <button className='btn btn-danger mx-2' onClick={()=>navigate('/')}>annuler</button>
      </div>
      </form>
    </div>
  )
}
