import {React, useState} from "react";
import participants from "./ListeParticipants";
import { Link, useParams } from "react-router-dom";
export default function AddParticipant() {
  const {id} = useParams();
  const [nom, setNom] = useState();
  function Add(){
    if(nom){
      participants.push({id: participants.length + 1, nom: nom, idCourse: id})
    }
    return ;
  }
  return (
    <>
      <h1>Nouveau participant</h1>
      ID Participant <input type="text" value={participants.length + 1} readOnly /> <br />
      Nom <input type="text" onChange={(e)=> setNom(e.target.value)}/> <br />
      ID course <input type="text" readOnly value={id}/> <br />
      <Link to="/">
        <button className="btn btn-primary">Cancel</button>
      </Link>
      <Link to="/">
        <button className="btn btn-primary" onClick={Add}>Ok</button>
      </Link>
    </>
  );
}
