    setNewItem('')
  }
  function deletItem(id){
    const newArry=items.filter(item=>item.id!==id)
    setItems(newArry)
  }
  return (
    <div className='text-center'>
      <h2>Todo List App</h2>
      <input type='text' placeholder='add an item...' onChange={(e)=>setNewItem(e.target.value)}/>
      <button onClick={()=>addItem()}>add</button>
      <ul>
        {items.map(item=>{
          return(
            <li>{item.value} <button onClick={()=>deletItem(item.id)}>sup</button></li>
          )
        })}
      </ul>
    </div>
  )
}
