import React,{useState} from 'react';
export default function App() {
  const trains=[
    {id:'t1',nom:'Buraq',villes:[]},
    {id:'t2',nom:'Gazel', villes:[]},
    {id:'t3',nom:'Eagle-Wins', villes:[]},
    {id:'t4',nom:'Fennec', villes:[]},
    ]
    const [id,setId]=useState()
    const [train,setTrain]=useState()
    const [ville,setVille]=useState('')
    const [message, setMessage]=useState('')

    function rechercher(){
      if(id){
        setTrain( trains.find((t)=> t.id==id))
      }else{
        setMessage('le id pas trouveé')
      }
    }

    function ajouterVille(){
      if(train){
          train.villes.push(ville);
          setMessage('Ville ajouté aux villes de passage')
      }
  }
  return (
    <div className='d-flex justify-content-center'>
    <div className='card shadow-lg p-3 m-2 col-8'>
      <h4 className='text-primary'>Gestion des trains</h4>

     <table >
      <tr>
        <td><span className='fw-bold'>Id Train</span></td>
        <td><span><input type='text' onChange={(e)=>setId(e.target.value)} className='form-control' style={{width:'60%'}}/></span></td>
        <td><button className='btn btn-primary'onClick={rechercher}>rechercher</button></td>
      </tr>
      <tr >
        <td><span className='fw-bold'>Nom</span></td>
        <td><span><input type='text' value={train?train.nom:''} readOnly style={{width:'90%'}}/></span></td>
      </tr>
      <tr>
        <td className='fw-bold'>Ville Depart</td>
        <td><span><input type='text' value={train && train.villes.length>0?train.villes[0]:''} readOnly/></span></td>
      </tr>
      <tr>
        <td className='fw-bold'>Ville terminus</td>
        <td><span><input type='text' value={train && train.villes.length>0?train.villes[train.villes.length-1]:''} readOnly/></span></td>
      </tr>
      <tr>
        <td><span className='fw-bold'>Nouvelle ville de passage</span></td>
        <td><span><input type='text' className='form-control' style={{width:'50%'}}/></span></td>
        <td><button className='btn btn-primary'>Ajouter la ville</button></td>
      </tr>
     </table>
     <h5 className='text-danger text-center'>{message}</h5>
     {train && train.villes.length>0?
            <div>
                <h2>liste des villes de passage</h2>
                <table>
                    <tr>
                        <td>Nom de la ville  </td>
                        <td>Ordre de passage</td>
                        <td></td>
                    </tr>
                    {
                        train.villes.map((v,i)=> {
                          return(
                          <tr>
                             <td>{v}</td>
                             <td>{(i+1)}</td>
                             <td><button className='btn btn-danger' >Supprimer</button></td>
                          </tr>

                        )})
                    }
                </table>
            </div>
            :''}
    </div>
    </div>
  )
}
