import './App.css';
import React, { Component } from 'react';
 class App extends Component {
  state={
    nom:'Mohammed',
    age:20,
  }
  aficher=()=>{
  return <>
   {this.state.nom} 
        </>
  }
  chnge=()=>{
    return this.setState({
      nom:'saad',
      age:13
    })
  }
  
  

  render() {
    return (
      <div>
        votre nom est : {this.aficher()}
        <button onClick={()=>this.chnge()}>click</button>
      </div>
    )
  }
}
export default App











