import React, { useState } from 'react';
import './App.css';
function App() {
  const [search,setSerch]=useState('');
  const handleinput=(e)=>{
    setSerch(e.target.value)
    if(e.target.value.length>10){
      setSerch(e.target.value='stop')
    
    }
  }
  return (
    <div className='App'>
      <input type="text" value={search} onChange={handleinput}/>
      <div>{search}</div>
    </div>
  )
}
export default App
