import { React } from "react";

const Buteurs = [
  {
    id: 1,
    player: { player_id: 19779, player_name: "Karim Benzima" },
    teams: "Real Madrid",
    goals: { home: 7, away: 7 },
  },
  {
    id: 2,
    player: { player_id: 4934, player_name: "Robert Lewandowski" },
    teams: "FC Barcelona",
    goals: { home: 9, away: 4 },
  },
  {
    id: 3,
    player: { player_id: 4930, player_name: "Mohamed Salah" },
    teams: "FC Barcelona",
    goals: { home: 3, away: 3 },
  },
];
export default function App() {
  // function maxBut(){
