  //       if(Buteurs[index + 1]){
  //         if(maxButAway < Buteurs[index + 1].goals.away){maxButAway=Buteurs[index].goals.away}
  //       }
  //       index++;

  //     return maxButAway;
  //   }
  // }
  const Buteurs2 = [...Buteurs, {
    id: 4,
    player: { player_id: 500, player_name: "Halland" },
    teams: "M.City",
    goals: { home: 9, away: 9 },
  }]
  function maxBut() {
    const sortB = Buteurs.sort((a, b) =>
      a.goals.home > b.goals.home ? 1 : -1
    );
    return sortB[Buteurs.length - 1].goals.home;
  }
  function equipeM() {
    const sortB = Buteurs.sort((a, b) =>
      a.goals.away > b.goals.away ? 1 : -1
    );
    return sortB[Buteurs.length - 1].teams;
  }
  return (
    <>
      <h1>Q1</h1>
      {Buteurs.map((buteur, index) =>
        buteur.player.player_name === "Mohamed Salah" ? (
          <p key={index}>
            Total but Du Mohamed Salah :{" "}
            <b>{buteur.goals.home + buteur.goals.away}</b>
          </p>
        ) : (
          ""
        )
      )}
      <hr />
      <h1>Q2</h1>
      Les joueurs marque ayont plus de 3 buts away :
      {Buteurs.map((buteur, index) =>
        buteur.goals.away > 3 ? <h4>{buteur.player.player_name}</h4> : ""
      )}
      <hr />
      <h1>Q3</h1>
      {maxBut()}
      <hr />
      <h1>Q4</h1>
      {Buteurs.reduce((total, current) => total + +current.goals.away, 0)}
      <h1>Q5</h1>
      {equipeM()}
      <hr />
      <h1>Q6</h1>
        {
          Buteurs2.map((but, index)=> <p key={index}>- {but.player.player_name}</p>)
        }

    </>
  );
}
