import React, { useEffect, useState } from "react";
import axios from "axios";
export default function App() {
  const [id, setId] = useState(1);
  const [utilisateur, setUtilisateur] = useState();

  useEffect(() => {
    axios
      .get(`https://jsonplaceholder.typicode.com/users/${id}`)
      .then((rslt) => {
        setUtilisateur(rslt.data);
      });
  });
  return (
    <div>
      <label>donner le id:</label>
      <input
        type="text"
        onChange={(e) => setId(e.target.value)}
        value={id}
      ></input>
      {utilisateur ? (
        <div className="child" key={utilisateur.id}>
          <h3 style={{ color: "rgb(92, 62, 3)" }}>
            le nom: {utilisateur.name} {utilisateur.username}
          </h3>
          <p>email:{utilisateur?utilisateur.email:''}</p>
          <p> telephone:{utilisateur?utilisateur.phone:''} </p>
          <p> site web:{utilisateur?utilisateur.website:''} </p>
          <p> rue : {utilisateur.address?utilisateur.address.street:''} </p>
          <p> ville : {utilisateur?utilisateur.address.city:''} </p>
        </div>
      ) : (
        <div>svp choisir un id valide!!!!</div>
      )}
    </div>
  );
}
