import {useState,useEffect} from 'react'

const trains=[
    {id:'t1',nom:'Buraq',villes:[]},
    {id:'t2',nom:'Gazel', villes:[]},
    {id:'t3',nom:'Eagle-Wins', villes:[]},
    {id:'t4',nom:'Fennec', villes:[]},
]

export default function App(){

    const [id,setId]=useState()
    const [train,setTrain]=useState()
    const [ville,setVille]=useState('')


    function rechercher(){
            setTrain( trains.find((t)=> t.id==id))
    }

    function ajouterVille(){
        if(train){
            train.villes.push(ville);

        }
    }

    function supprimer(i){
        train.villes.splice(i, 1)

    }

    return(
        <div style={{margin:"30px"}}>
            <h2>Gestion des trains</h2>
            <h2>Id: <input type="text" onChange={(e)=>setId(e.target.value)}/>
            <button onClick={rechercher}>rechercher</button>
            </h2>
            <h2>Nom du train: {train?train.nom:''} </h2>
            <h2>Volle de dÃ©part:{train && train.villes.length>0?train.villes[0]:''} </h2>
            <h2>Ville terminus: {train && train.villes.length>0?train.villes[train.villes.length-1]:''} </h2>
            <br></br>
            <h2>Id: <input type="text" onChange={(e)=>setVille(e.target.value)}/>
            <button onClick={ajouterVille}>Ajouter ville</button>
            </h2>
            {train && train.villes.length>0?
            <div>
                <h2>liste des villes de passage</h2>
                <table>
                    <tr>
                        <td>Nom de la ville  </td>
                        <td>Ordre de passage</td>
                        <td></td>
                    </tr>
                    {
                        train.villes.map((v,i)=> {return(
                          <tr>
                             <td>{v}  </td>
                             <td>{(i+1)}</td>
                             <td><button onClick={()=>supprimer(i)} >Supprimer</button></td>
                          </tr>

                        )})
                    }
                </table>
            </div>
            :''}

        </div>
    )
}
