import './App.css'
import React, { useState } from "react";
function App() {
  const [count,setCount]=useState("red");
  return (
    <div> 
      <button onClick={()=>setCount(count("yellow"))}>yellow</button> 
      <div> {count}</div>
     
    
    </div>
  )
}

export default App
