import React, { useState } from 'react'
import Comp1 from './components/Comp1';
export const GlobalConter =React.createContext()
export default function App() {
  const[conter,setConter]=useState(0)
  return (
    <div>

      <nav className='d-flex justify-content-around bg-primary text-light'>
        <span>Accueil</span><span>About</span><span>Contact</span>
        <span className='bg-dark p-2 text-light'>{conter}</span>
      </nav>
      <GlobalConter.Provider value={()=>setConter(conter+1)}>
        <Comp1/>
      </GlobalConter.Provider>

    </div>
  )
}
