import React, { Component } from 'react';
 class App extends Component {
  state={
    nom:'Mohammed',
    age:20,
  }
  aficher=()=>{
    this.state.nom
  }
  render() {
    return (
      <div>
        votre nom est : {this.state.nom}
      </div>
    )
  }
}
export default App











