import React from 'react';
import './App.css';
import { useState } from 'react'
export default function App() {
    const list=[
        { id:1,
          name:"HP",
          price:3000
        },
        { id:2,
          name:"samsung",
          price:4500
        },
        { id:3,
          name:"lenovo",
          price:5000
        },
      ]

      const [lists,setLists]=useState(list)
      const[total,setTotal]=useState(0)
      function handleSubmit(){
        const name=document.getElementById('name').value;
        const price=document.getElementById('price').value;
        const newlist={
          id:lists.length+1,
          name,
          price
        }
        setLists((l)=>l.concat(newlist))
        setTotal(list.price)
      }
      return (

<div className='text-center'>
<input type='text' id='name'/>
     <br/>
     <input type='text' id='price'/>
     <br/>
     <button onClick={handleSubmit}>valid</button>

          <table style={{border:"1px solid red"}}>
            <tr style={{border:"1px solid red"}}>
              <th style={{border:"1px solid red"}}>nom produit</th>
              <th style={{border:"1px solid red"}}>price</th>
              <th colspan='2' style={{border:"1px solid red"}}>action</th>
            </tr>
            {
              lists.map((e)=>
                <tr style={{border:"1px solid red"}}>
                  <td style={{border:"1px solid red"}}>{e.name}</td>
                  <td>{e.price}</td>
                  <td><button>Delete</button></td>
                </tr>
              )
            }
          </table>
          <div> total est: {total}</div>
        </div>
       
      )
}
