import React from 'react'
import ProductListItems from './ProductListItems'
import '../App.css'
export default function ProductList({products,addTocart}) {
  return (
    <div className='row' >
          <div className='d-flex justify-content-center gap-1 my-3'>
         <select>
          {products.map((options,index)=>{
            return <option key={index+1} value={options.category}>{options.category}</option>
          })}
        </select><button className="btn btn-success">Search</button>
      </div>
      {products.map(prd=><ProductListItems key={prd.id} product={prd} addTocart={addTocart}/>)}
    </div>
  )
}
