import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import EditTable from './EditTable';
import { Ajouter ,Supremier,Update} from './store'
export default function AfficherTab() {
    
    let afichage=useSelector((data)=>data.table.tbl)
    const dispatch=useDispatch()
    const[nomP,setnomP]=useState()
    const[prix,setPrix]=useState()
    const[editStat,seteditStat]=useState(false)

    const handlClick=()=>{
        dispatch(Ajouter({
            id:afichage.length+1,
            nomP:nomP,
            prixP:prix
        }))
    }

    function handlEdit(id){
        seteditStat(id)
      }

  


  return (
    <div>
        <input type='text' onChange={(e)=>setnomP(e.target.value)}/>
        <input type='text' onChange={(e)=>setPrix(e.target.value)} />
        <button onClick={handlClick}>Ajouter</button><br/>
        <table>
            <tr>
                <th>nom produis</th>
                <th>prix produis</th>
                <th>Action</th>
            </tr>

            
            {/* {afichage.length>0?afichage.map((rslt,indx)=>{
                return editStat===indx+1?<EditTable rslt={rslt} Update={dispatch(Update)}/>:<tr>
                    <td>{rslt.nomP}</td>
                    <td>{rslt.prixP}</td>
                    <td>
                        <button onClick={()=>dispatch(Supremier(rslt.id))}>delete</button>
                        <button onClick={()=>handlEdit(indx+1)}>Edit</button>
                    </td>
                </tr>
            }):''} */}
        </table>
    </div>
  )
}
