import React,{useState} from 'react';
const trains=[
  {id:'t1',nom:'Buraq',villes:[]},
  {id:'t2',nom:'Gazel', villes:[]},
  {id:'t3',nom:'Eagle-Wins', villes:[]},
  {id:'t4',nom:'Fennec', villes:[]},
  ]
export default function App() {
    const [id,setId]=useState()
    const [list,setLists]=useState()
    const [ville,setVille]=useState()
    const [message, setMessage]=useState()

    function rechercher(){
      if(id){
        setLists(trains.find((t)=> t.id===id))
      }
    }

    function ajouter_ville(){
    if(list){
      list.villes.push(...ville)
      setMessage('Ville ajouté aux villes de passage')
    }}
  
    function supremier(i){
      // list.find((elm)=>elm.id!==id)
       list.villes.splice(i,1)
      setMessage("Ville supprimée du parcours")
    }
 
  return (
    <div className='d-flex justify-content-center'>
    <div className='card shadow-lg p-3 m-2 col-8'>
      <h4 className='text-primary'>Gestion des trains</h4>

     <table>
      <tr>
        <td><span className='fw-bold'>Id Train</span></td>
        <td><span><input type='text' onChange={(e)=>setId(e.target.value)} className='form-control' style={{width:'60%'}}/></span></td>
        <td><button className='btn btn-primary'onClick={rechercher}>rechercher</button></td>
      </tr>
      <tr >
        <td><span className='fw-bold'>Nom</span></td>
        <td><span><input type='text' value={list?list.nom:''} readOnly style={{width:'90%'}}/></span></td>
      </tr>
      <tr>
        <td className='fw-bold'>Ville Depart</td>
        <td><span><input type='text' value={list && list.villes.length>0?list.villes[0]:""} readOnly/></span></td>
      </tr>
      <tr>
        <td className='fw-bold'>Ville terminus</td>
        <td><span><input type='text' value={list && list.villes.length>0?list.villes[list.villes.length-1]:""} readOnly/></span></td>
      </tr>
      <tr>
        <td><span className='fw-bold'>Nouvelle ville de passage</span></td>
        <td><span><input type='text' className='form-control'onChange={(e)=>setVille(e.target.value)} style={{width:'50%'}}/></span></td>
        <td><button className='btn btn-primary' onClick={ajouter_ville} >Ajouter la ville</button></td>
      </tr>
     </table>
     <h5 className='text-center my-3 text-light bg bg-success p-2 rounded'>{message}</h5>
     {list && list.villes.length>0?
            <div>
                <h2>liste des villes de passage</h2>
                <table  className='table-striped-columns border border-primary mt-4 col-8 text-center'>
                    <tr className='border border-primary'>
                        <td className='border border-primary'>Nom de ville  </td>
                        <td className='border border-primary'>Ordre de passage</td>
                        <td className='border border-primary'>Action</td>
                    </tr>
                    {
                        list.villes.map((v,i)=>{
                          return(<tr className='border border-primary'>
                             <td className='border border-primary'>{v}</td>
                             <td className='border border-primary'>{(i+1)}</td>
                             <td className='border border-primary'>
                              <button className='btn btn-danger' onClick={()=>supremier(i)}>Supprimer</button></td>
                          </tr>
                        )})
                    }
                </table>
            </div>
            :''}

    </div>
    </div>
  )
}
