import React from 'react'
import { useParams } from 'react-router'

export default function Modifier() {
  const [name,setName]=useState()
  const [SurfaceArea,setSurfaceArea]=useState()
  const [Population,setPopulation]=useState()
  const count=useSelector(data=>data.length)
  const dispatch= useDispatch()
  const navigit=useNavigate()
  
  const handSubmit=()=>{
  dispatch(AjouterAction({
    id:count+1,
    nm:name,
    surfce:SurfaceArea,
    pupl:Population
  }))
  navigit('/')

  }
    const {id}=useParams()
  return (
    <div>
    <form >
     <div>
     <label>name</label>
      <input type="text" value={name} onChange={(e)=>setName(e.target.value)}/>
     </div>

     <div>
     <label>SurfaceArea</label>
      <input type="text" value={SurfaceArea} onChange={(e)=>setSurfaceArea(e.target.value)}/>
     </div>

     <div>
     <label>Population</label>
      <input type="text" value={Population} onChange={(e)=>setPopulation(e.target.value)}/>
     </div>
     <button onClick={handSubmit}>valider</button>
    </form>
  </div>
  )
}
