 function affiche(){
  setInfo(txt)
  }
  return (
    <div className='text-center'>
      <input type='text' onChange={(e)=>setTxt(e.target.value)}/>
      <button onClick={affiche}>afficher</button>
      <p>{info===""}</p>
      <h1>fddddddddddddddddddddd</h1>
    </div>
  )
}













