import React, { Component } from 'react';
import './Home.css';
import imgs from './pngegg.png'
class Home extends Component {
    state={
         myArry:[
            {id:'1', name:"mohamed"},
            {id:'2', name:"Ali"},
            {id:'3', name:"walid"}
        ]
    }
    Delet=(id)=>{
        // console.log(id)
        const myArry=[...this.state.myArry];
        myArry.splice(id,1);
        this.setState({
            myArry
        })
    }
    render() { 
   
        return (
            <div className='parnt'>
                <div className='titre'>
                    <img src= {imgs}/>
                    <h1 className='ecoleTitre'>Ecole Marhaba</h1>
                </div>
                <div className='barTitre'>
                    <h2 className='listelve'>La liste des Ã©leves</h2>
                </div>
                <div className='rslt'>
                <ol>
                  {this.state.myArry.map((todo,id)=>
                  <li key={id}>{todo.name}
                  <button id='btndelet' onClick={()=>{this.Delet(id)}}>Delete</button></li>
                    )}
                    <input type='text' id='inpt' pleaceholder='entre in eleve'/>
                </ol>
                </div>
            </div>
        );
    }
}
 
export default Home;
