import React, { useState } from 'react'
export default function App() {
  const vols=[ 
    {numVol:1,depart:"Fes",Arrivee:"Madrid", 
    places:[ 
    {numPlace:1,tarif:1000, libre:true}, 
    {numPlace:2,tarif:1100, libre:true }, 
    {numPlace:3,tarif:11100, libre:true }, 
    ]}, 
    {numVol:2,depart:"Fes",Arrivee:"London", 
    places:[ 
    {numPlace:1,tarif:2500, libre:true }, 
    {numPlace:2,tarif:3000, libre:true }, 
    {numPlace:3,tarif:2500, libre:true }, 
    ]}, 
    {numVol:3,depart:"Fes",Arrivee:"Bern", 
    places:[ 
    {numPlace:1,tarif:3500, libre:true }, 
    {numPlace:2,tarif:3000, libre:true}, 
    {numPlace:3,tarif:4000, libre:true}, 
    ]} 
   ] 
   const[numVol,setnumVol]=useState()
   const[vol,setvol]=useState(vols[0])
   const [total,setTotal]=useState(0)



   function rechercher(){
    if(!numVol){
      alert('vide')
    }else{
       setvol(vols.find((e)=>e.numVol==numVol))
    }
   }

   function reservations(i){
    const v=vols.find((x)=>x.numVol===vol.numVol)
    const p=v.places
    p[i].libre=false
    setvol(prevent=>{
      return{...prevent,p}
    })
   }
  return (
    <div>
 
      <h4>reservations de places d'un vol</h4>
      <div className='m-2'>
        <b>No Vol</b>
        <input type='text' id='NoVol' onChange={(e)=>setnumVol(e.target.value)}/>
        <button onClick={rechercher}>rechercher</button>
      </div>

      <div className='m-2'>
        <b>depart</b> 
        <input type='text' readOnly value={vol?vol.depart:""}/>
      </div>

      <div className='m-2'>
        <b>arriv√©e</b> 
        <input type='text' readOnly value={vol?vol.Arrivee:""}/>
      </div>

      <div>
        <b className='text-primary'>Liste des places </b>
        <span >Prix total</span ><b className='text-danger'></b>
      </div>
      <div>
        <table>
          <tr>
            <th>No de place</th>
            <th>Tarif</th>
            <th>action</th>
          </tr>
          {affch.filter((fl)=>{
            return fl.numVol===numVol
          }).map((ex,id)=>{
            return <tr>
              <td>{ex.depart}</td>
              <td>{ex.Arrivee}</td>
            </tr>
          })
          }
        </table>
      </div>
     
    </div>
  )
}
