import React,{useState} from 'react';

const Form = () => {
    const [name, setName] = useState();
    const [lastName, setLastName] = useState();
    const [age, setAge] = useState();

    const [info, setInfo] = useState('');

    const show = () => {
        setInfo(`you are ${name} ${lastName} and your age is ${age}`);
    }

    return (
        <div>
            name :
            <input type="text" value={name} on onChange={(event) => setName(event.target.value)} />
            <br />
            lat name :
            <input type="text" value={lastName} on onChange={(event) => setLastName(event.target.value)} />
            <br />
            age :
            <input type="number" value={age} on onChange={(event) => setAge(event.target.value)} />
            <br />
            <button onClick={show}>show</button>
            <p> {info} </p>
        </div>
    );
}

export default Form;
