//================ex3
import React,{useState} from 'react';

const App = () => {
    const velos = [
        {ref:'v1', marque:'ford', autonomie:0, kilometrage:0, velos:[{codeP:'1',distance: 20},{codeP:'2',distance: 30}] },
        {ref:'v2', marque:'ford', autonomie:0, kilometrage:0, velos:[{codeP:'1',distance: 20},{codeP:'2',distance: 30}] },
        {ref:'v3', marque:'ford', autonomie:0, kilometrage:0, velos:[{codeP:'1',distance: 20},{codeP:'2',distance: 30}] },
        {ref:'v4', marque:'ford', autonomie:0, kilometrage:0, velos:[{codeP:'1',distance: 20},{codeP:'2',distance: 30}] }
    ]

    const [veloRef, setVeloRef] = useState()
    const [nombreMinute, setNombreMinute] = useState()
    const [distance, setDistance] = useState()
    const [velo, setVelo] = useState([])

    function Rechercher()
    {
        setVelo(velos.find((v) => v.ref === veloRef));
    }

     function chargerVelo(){
        if(velo){
            if(velo.autonomie+ nombreMinute<100){
                velo.autonomie+=nombreMinute
            }else{
                velo.autonomie=100
            }
        }
    }
    
    function ajouter_velos()
    {
      if(velo.autonomie > distance){
        velo.velos.push({codeP:velo.velos.length+1, distance:distance});
        setVelo((prev)=>({...prev,velos:velos.distance}))}
    }

  //   function ajouter_ville(){
  //     list.villes.push({idV:list.villes.length+1,nomV:ville})
  //     setLists((prev)=>({
  //       ...prev,villes:list.villes
  //     })
  //     )
  // }

    return (
        <div>
            <h1>Suivi des vélos Electriques</h1>
            reference <input type="text" onChange={(event) => setVeloRef(event.target.value)} />
             <button onClick={() => Rechercher()}>Rechercher</button><br/>
            Marque :{(velo)?velo.marque:''}<br />
            Autonomie en [km] :{(velo)?velo.autonomie:''}<br />
            kilométrage :{(velo)?velo.kilometrage:''}<br />
            nombre de mintute <input type="text" onChange={(event) => setNombreMinute(Number(event.target.value))}  />
            <button onClick={() => chargerVelo()}>charger</button> <br />

            distance de velos <input type="text"  onChange={(event) => setDistance(event.target.value)}  />
            <button onClick={ajouter_velos}>ajouter velos</button>
            <br />
            <table>
                <tr>
                    <th>id velos</th>
                    <th>distance</th>
                </tr>

                {
                    (velo.velos)?velo.velos.map((velo) => {
                        return <tr>
                            <td> {velo.codeP} </td>
                            <td> {velo.distance} </td>
                        </tr> 
                    }):''
                }

            </table>
        </div>
    );
}

export default App;
