import React,{useState} from 'react';
const trains=[
  {id:'t1',nom:'Buraq',villes:[]},
  {id:'t2',nom:'Gazel', villes:[]},
  {id:'t3',nom:'Eagle-Wins', villes:[]},
  {id:'t4',nom:'Fennec', villes:[]},
  ]
export default function App() {
    const [id,setId]=useState()
    const [list,setLists]=useState()
    const [ville,setVille]=useState()
  

    function rechercher(){
      if(id){
        setLists(trains.find((t)=>t.id===id))
      }
    }

    function ajouter_ville(){
      list.villes.push({idV:list.villes.length+1,nomvil:ville})
      setLists((prev)=>({
        ...prev,villes:list.villes
      })
      )
  }
  
    function supremier(i){
      //list.villes.filter((elm)=>elm.idV!==idV)
       list.villes.splice(i,1)
    }
 
  return (
    <div className='d-flex justify-content-center'>

    <div className='shadow-lg p-3 m-2 col-8'>
      <h4 className='text-primary'>Gestion des trains</h4>
      <input type='text' onChange={(e)=>setId(e.target.value)}/>
      <button onClick={rechercher}>rechercher</button>

       <div className='mt-3'><b>Nom</b> {list?list.nom:""}</div>
       <div className='mt-3'><b>Nom</b> {list?list.villes[0].nomvil:""}</div>
       <div className='mt-3'><b>Nom</b> {list?list.nom:""}</div>

      <div>
        <b className='fw-bold'>Nouvelle ville de passage</b>
        <input type='text'onChange={(e)=>setVille(e.target.value)} />
        <button className='btn btn-primary' onClick={ajouter_ville} >Ajouter la ville</button>
      </div>
      <h2>liste des villes de passage</h2>
      <table>
        <tr>
          <th>nom de ville</th>
          <th>id de passage</th>
          <th>action</th>
        </tr>
        {list?list.villes.map((l)=>{
          return<tr key={l.idV}>
            <td>{l.nomvil}</td>
            <td>{l.idV}</td>
            <td><button onClick={()=>supremier(l.idV)}>delete</button></td>
          </tr>
        }):''}
      </table>

    </div>
    </div>
  )
}
