import { React, useState } from "react";

const trains = [
  {
    id: "t1",
    nom: "Buraq",
    villes: [
      { nomV: "fes", ordreP: 10 },
      { nomV: "meknes", ordreP: 20 },
    ],
  },
  {
    id: "t2",
    nom: "Gazel",
    villes: [
      { nomV: "casa", ordreP: 10 },
      { nomV: "meknes", ordreP: 20 },
    ],
  },
  {
    id: "t3",
    nom: "Eagle-Wins",
    villes: [
      { nomV: "asfi", ordreP: 10 },
      { nomV: "meknes", ordreP: 20 },
    ],
  },
  {
    id: "t4",
    nom: "Fennec",
    villes: [
      { nomV: "laayoun", ordreP: 10 },
      { nomV: "meknes", ordreP: 20 },
    ],
  },
];

function App() {
  const [idT, setIdT] = useState();
  const [train, setTrain] = useState(trains);
  const [ajoutV,setAjoutV] = useState();
  function Recherche(){
  if(idT) setTrain(trains.find(train => train.id === idT));
  }
  function Ajouter(){
    if(ajoutV && train){
      const villeA = train.villes.push({nomV : ajoutV,
      orderP : train.villes[train.villes.length - 1].ordreP + 10})
      setTrain(prevState => ({...prevState, villeA}))
    }
  }
  function Supp(index){
    train.villes.splice(index,1);
    setTrain({...train, villes : train.villes})
  }
  return (
    <>
      <h1>Gestion des trajets de trains</h1>
      <div className="d-flex  justify-content-between w-25 m-5 text-center">
        <label htmlFor="idT">Id Train</label>
        <input type="text"  onChange={(e) => setIdT(e.target.value)} />
        <button className="btn btn-primary" onClick={() => Recherche(idT)}>Rechercher</button>
      </div>
      <div className="d-flex  justify-content-between w-25 m-5 text-center">
        <label htmlFor="nom">Nom</label>
        <input type="text" readOnly value={train?train.nom:""}/>
      </div>
      <div className="d-flex  justify-content-between w-25 m-5 text-center">
        <label htmlFor="villeD">Ville depart</label>
        <input type="text" readOnly value={train.villes?train.villes[0].nomV:""}/>
      </div>
      <div className="d-flex  justify-content-between w-25 m-5 text-center">
        <label htmlFor="villeT">Ville terminus</label>
        <input type="text" readOnly value={train.villes?train.villes[train.villes.length - 1].nomV:""}/>
      </div>
      <div className="d-flex  justify-content-between w-50 m-5 text-center">
        <label htmlFor="nvV">Nouvelle ville de passage</label>
        <input type="text" onChange={(e) => setAjoutV(e.target.value)}/>
        <button className="btn btn-primary" onClick={() => Ajouter()}>Ajouter la ville</button>
      </div>
      <table className="w-25">
        <thead>
          <tr>
            <th>Nom de ville</th>
            <th>order passage</th>
          </tr>
        </thead>
        <tbody>
          {train.villes?train.villes.map((train, index) => (
            <tr className="text-center" key={index}>
              <td>{train.nomV}</td>
              <td>{train.ordreP}</td>
              <td><button className="btn btn-primary" onClick={() => Supp(index)}>Supprimer</button></td>
            </tr>
          )): ""}
        </tbody>
      </table>
    </>
  );
}

export default App;

