import React, { useState } from 'react'
import { useEffect } from 'react'
export default function App() {
  const[nom,setNom]=useState('ahmed')
  const[prenom,setPreom]=useState('alami')
  const[age,setAge]=useState(20)
  function changeNom(e){
    setNom(e.target.value)
  }
  function changePrenom(e){
    setPreom(e.target.value)
  }
  function changeAge(e){
    setAge(e.target.value)
  }
  useEffect(()=>{
    document.title=nom
  },[nom])
  return (
    <div>
      <h3>{nom}</h3>
     <div> <b>nom</b>  <input type='text'  onChange={changeNom} />  </div>
     <div> <b>prenom</b>  <input type='text' onChange={changePrenom}/> </div>
     <div> <b>age</b>  <input type='text' onChange={changeAge}/> </div>
    </div>
  )
}
