import React,{useState} from 'react';

const App = () => {

    const trins = [
        {
          id: "t1",
          nom: "Buraq",
          villes: [
            { nomV: "fes", ordreP: 10 },
            { nomV: "meknes", ordreP: 20 },
          ],
        },
        {
          id: "t2",
          nom: "Gazel",
          villes: [
            { nomV: "casa", ordreP: 10 },
            { nomV: "meknes", ordreP: 20 },
          ],
        },
        {
          id: "t3",
          nom: "Eagle-Wins",
          villes: [
            { nomV: "asfi", ordreP: 10 },
            { nomV: "meknes", ordreP: 20 },
          ],
        },
        {
          id: "t4",
          nom: "Fennec",
          villes: [
            { nomV: "laayoun", ordreP: 10 },
            { nomV: "meknes", ordreP: 20 },
          ],
        },
      ];

    const [traunID, setTrainId] = useState()
    const [tab, setTab] = useState(trins)
    const[ville,setVille]=useState()

    function Rechercher()
    {
        setTab(trins.find((trn) => trn.id == traunID));
    }

     function ajouter_ville()
    {
        tab.villes.push({ordreP:(tab.villes.length+1)*10, nomV:ville});
        setTab((prev)=>({
            ...prev,villes:tab.villes
        }))
    }


    function supremier(i){
       setTab(tab.villes.filter((f)=>f.ordreP!==i))
    }
    return (
        <div>
            <h1>Suivi des vélos Electriques</h1>
            id <input type="text" onChange={(event) => setTrainId(event.target.value)} /> <br />
            nom <input type="text" value={(tab)?tab.nom:''} />
            <button onClick={() => Rechercher()}>Rechercher</button> <br />
           ville depart: <b>{tab.villes?trin.villes[0].nomV:""}</b>
            ville arrivée :<b>f</b>
            nouvelle_ville de passage <input type='text' onChange={(e)=>setVille(e.target.value)}/>
            <button onClick={()=>ajouter_ville()}>Ajouter la ville</button>
            <table>
                <tr>
                    <th>id parcours</th>
                    <th>distance</th>
                </tr>

                {
                    (tab.villes)?tab.villes.map((v) => {
                        return <tr>
                            <td> {v.nomV} </td>
                            <td> {(v.ordreP)} </td>
                            <td> <button onClick={()=>supremier(v.ordreP)}>delete</button> </td>
                        </tr> 
                    }):''
                }

            </table>
        </div>
    );
}

export default App;
