import React from 'react'
import img1 from "./pictures/HP16D0195NF.jpg"
export default function App() {
  const products = [
    {
    id: 1,
    title: 'PC Portable Gamer HP VICTUS',
    price: '7490 DH',
    thumbnail : 'HP16D0195NF.jpg'
    },
    {
    id: 2,
    title: 'PC Portable Gamer HP VICTUS',
    price: '2190 DH',
    thumbnail : 'HP14424U3EA.jpg'
    },
    {
    id: 3,
    title: 'Pc Portable Chromebook Acer',
    price: '3640 DH',
    thumbnail: 'NXATHEF002.jpg'
    },
    {
    id: 4,
    title: 'PC Portable - HUAWEI',
    price: '1270 DH',
    thumbnail: 'HUA6901443442959.jpg'
    }];
  return (
    <div className='d-flex justify-content-center'>
      <div className='card shadow-sm m-2 p-2'>
        <h3 className='text-primary'>Ordinateurs portable </h3>
          {products.map((elm,index)=>{
            return  <div key={index} className="col">
            <div  className="card shadow-sm">
            <img className="bd-placeholder-img card-img-top"src={img1} alt="" />
            <div className="card-body">
            <p className="card-title">{elm.title}</p>
            <p className="card-text">{elm.price}</p>
            <div className="d-flex justify-content-between align-items-center">
              <div className="btn-group">
              <button type="button" className="btn btn-sm btn-outline-secondary">Ajouter au panier</button>
              </div>
            </div>
            </div>
          </div>
          </div>
          })}
      </div>
    </div>
  )
}
