import React, { useState } from 'react'
export default function App() {
  const vols=[ 
    {numVol:1,depart:"Fes",Arrivee:"Madrid", 
    places:[ 
    {numPlace:1,tarif:1000, libre:true}, 
    {numPlace:2,tarif:1000, libre:true }, 
    {numPlace:3,tarif:1000, libre:true }, 
    ]}, 
    {numVol:2,depart:"Fes",Arrivee:"London", 
    places:[ 
    {numPlace:1,tarif:2500, libre:true }, 
    {numPlace:2,tarif:3000, libre:true }, 
    {numPlace:3,tarif:2500, libre:true }, 
    ]}, 
    {numVol:3,depart:"Fes",Arrivee:"Bern", 
    places:[ 
    {numPlace:1,tarif:3500, libre:true }, 
    {numPlace:2,tarif:3000, libre:true}, 
    {numPlace:3,tarif:4000, libre:true}, 
    ]} 
   ] 
   const[numVol,setnumVol]=useState()
   const[vol,setvol]=useState()
   const [total,setTotal]=useState(0)



   function rechercher(){
    if(!numVol){
      alert('vide')
    }else{
       setvol(vols.find((e)=>e.numVol==numVol))
    }
   }

   function reservations(i){
    const v=vols.find((x)=>x.numVol==numVol)
    const p=v.places[i].libre=false
    // const p=v.places
    // p[i].libre=false

    setvol(prevent=>{
      return{...prevent,places:p}
    })
    setTotal( vol.places.filter((x)=> x.libre===false).reduce((s,p)=> s+p.tarif,0))
   }
  return (
    <div>
 
      <h4>reservations de places d'un vol</h4>
      <div className='m-2'>
        <b>No Vol</b>
        <input type='text' id='NoVol' onChange={(e)=>setnumVol(e.target.value)}/>
        <button onClick={rechercher}>rechercher</button>
      </div>

      <div className='m-2'>
        <b>depart</b> 
        <input type='text' readOnly value={vol?vol.depart:""}/>
      </div>

      <div className='m-2'>
        <b>arriv√©e</b> 
        <input type='text' readOnly value={vol?vol.Arrivee:""}/>
      </div>

      <div>
        <b className='text-primary'>Liste des places </b>
        <span >Prix total</span ><b className='text-danger'>{total}</b>
      </div>
      <div>
        <table>
          <tr>
            <th>No de place</th>
            <th>Tarif</th>
            <th>action</th>
          </tr>
          {vol?vol.places.map((x,i)=>{
            return <tr key={i}>
            <td>{x.numPlace}</td>
            <td>{x.tarif}</td>
            <td>{
                x.libre
                ?<button onClick={()=> reservations(i)} >Reserver</button>
                :<button disabled >Reserver</button>
                }
            </td>
        </tr>
          }):""
          }
        </table>
      </div>
     
    </div>
  )
}
