import React, { useState } from 'react'
export default function App() {
  const [Item,setItem]=useState()
  const[afficher,setAfficher]=useState([])
  // function afch(){
  //   setAfficher(Item)
  // }
   function afch(){
    const item={
      id:Math.floor(Math.random()*1000),
      val:Item
    }
    setAfficher(oldlist=>[...oldlist,item])
  }
  function deletItm(id){
    const newAr=afficher.filter(it=>it.id!==id)
    setAfficher(newAr)
  }
  return (
    <div>
      <input type='text' onChange={(e)=>setItem(e.target.value)}/>
       <button onClick={()=>afch()}>add</button>
       <ul>
        {afficher.map(e=>{
          return(
            <li key={e.id} className='fs-3'>{e.val}
            <button className='btn btn-danger' onClick={()=>deletItm(e.id)}>DELETE</button> </li>
          )
        })}
       </ul>
    </div>
  )
}




















