import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { AjouterAction } from './Redux/InitialAction'
export default function Affihcer() {
  const [valu,setValu]=useState()
  const dispatch= useDispatch()
    const content=useSelector((data)=>data.val)
    const handlClick=()=>{
      dispatch(AjouterAction({
        code:Math.floor(Math.random()*1000),
        val:valu
      }))
    }
  return (
    <div>
        <input type='text' onChange={(e)=>setValu(e.target.value)}/>
        <button onClick={handlClick}>Valider</button>
        <div>
           
                {content.map((e)=>{
                    return  <ul>
                        <li key={e.id}>{e.elm}</li>
                    </ul>
                })}
            
        </div>
    </div>
  )
}
