import {React, useState} from 'react';
import { useDispatch } from 'react-redux';
import { add, edit } from './ContinentSlice';
const Q5 = () => {
  const dispatch  = useDispatch();
  const [nomC, setNomC] = useState();
  const [nomP, setNomP] = useState();
  const [population, setPopulation] = useState();
  const [cap, setCap] = useState();
  const [year, setYear] = useState();
  return (
    <div className='text-center m-2'>
      Name Continent : <input type="text" onChange={(e)=> setNomC(e.target.value)}/>  <br /><br />
      Name Pays : <input type="text" onChange={(e)=> setNomP(e.target.value)}/>  <br /><br />
      population : <input type="text" onChange={(e)=> setPopulation(e.target.value)}/> <br /><br />
      capital : <input type="text" onChange={(e)=> setCap(e.target.value)}/> <br /><br />
      indepYear : <input type="text" onChange={(e)=> setYear(e.target.value)}/> <br /><br />
      <button className='btn btn-primary m-2' onClick={() => dispatch(add(
{name : nomP, population: +population, capital : cap, indepYear : +year,nomC : nomC,}
      ))}>ADD PAYS</button>
      <button className='btn btn-primary' onClick={() => dispatch(edit({nomC : nomC, nomP : nomP,population : +population}))}>Edit PAYS</button>
    </div>
  );
}

export default Q5;
