import { useRef } from 'react';
import './App.css';
function App() {
  const firsname=useRef()
  return (
    <div>
      <input type="text" ref={firsname}/>
      <button onClick={()=>console.log(firsname.target.value)}>click</button>
      
    </div>
  );
}

export default App;
