import React, { useState } from 'react'
export default function App() {
    // const afch=[];
    const[lists,setLists]=useState([])
    const[nom,setNom]=useState()
    const[prix,setPrix]=useState()

    function remplir(){
        lists.afch.push({name:nom,Pris:prix})
        setLists((prev)=>({
            ...prev,afch:lists.afch
        }))
        alert('coco')
    }
  return (
    <div> 
        <input type="text" name="name" placeholder='Enter Name' onChange={(e)=>setNom(e.target.value)}/>
        <input type="text" name="price" placeholder='Enter price'onChange={(e)=>setPrix(e.target.value)}/>
        <button onClick={()=>remplir()} style={{ backgroundColor:"aqua"}}>add</button>
        <br/>
        {
            lists.afch?lists.afch.map((v)=>{
                return <tr>
                    <td>{v.nom}</td>
                    <td>{v.Pris}</td>
                </tr>
            }):""
        }
    </div>
  )
}
